mapper.update:
update hnt_sensor_info set
    sensor_name = #{chgSensorName}
where user_id = #{userId}
and sensor_uuid = #{sensorUuid}

mapper.delete:
delete from hnt_sensor_data
where uuid = #{sensorUuid}
limit #{batchSize}

mapper.select:
select concat(date_format(inst_dtm, '%Y-%m-%d %H:'), lpad(floor(minute(inst_dtm) / 30) * 30, 2, '0')) as inst_dtm , round(avg(sensor_value), 1) as sensor_value , inst_dtm as raw_inst_dtm from hnt_sensor_data where uuid = #{sensorUuid} and inst_dtm >= #{startDateTime} and inst_dtm <= #{endDateTime}
and inst_dtm > #{lastCursor}
group by date_format(inst_dtm, '%Y-%m-%d'), date_format(inst_dtm, '%Y-%m-%d %H'), floor(minute(inst_dtm) / 30) * 30, concat(date_format(inst_dtm, '%Y-%m-%d %H:'), lpad(floor(minute(inst_dtm) / 30) * 30, 2, '0')), inst_dtm order by inst_dtm asc limit #{pageSize}

select
    inst_dtm
    , sensor_value
    , uuid
from hnt_sensor_data
where uuid = #{sensorUuid}

    and inst_dtm > #{lastCursor}


    and inst_dtm >= #{startDateTime}


    and inst_dtm <= #{endDateTime}

order by inst_dtm asc
limit #{pageSize}

select
    inst_dtm
    , sensor_value
    , uuid
from hnt_sensor_data
where uuid = #{sensorUuid}

    and id > #{lastId}


    and inst_dtm >= #{startDateTime}


    and inst_dtm <= #{endDateTime}

order by id asc
limit #{pageSize}
