mapper.update:
update hnt_user set
    token = #{token}
where user_id = #{userId}

update hnt_user set
    login_dtm = #{loginDtm}
    , mdf_dtm = now()
where user_id = #{userId}

update hnt_user set
    logout_dtm = 'N'
where user_id = #{userId}

update hnt_user set
    last_login_dtm = #{activityStatus}
    , logout_dtm = #{focusStatus}
    , mdf_dtm = now()
where user_id = #{userId}

update hnt_user set
    logout_dtm = #{focusStatus}
    , mdf_dtm = now()
where user_id = #{userId}

mapper.delete:
delete from hnt_alarm
where user_id = #{userId}
and (urgent_yn = 'N' or urgent_yn = '')
